import { createMachine, interpret } from 'xstate';

const promiseMachine = createMachine({
    /** @xstate-layout  */
    id: 'promise',
    initial: 'pending',
    states: {
        pending: {
            on: {
                RESOLVE: { target: 'resolved' },
                REJECT: { target: 'rejected' }
            }
        },
        resolved: {
            type: "final"
        },
        rejected: {
            type: 'final'
        }
    }
});

export default interpret(promiseMachine)
export { promiseMachine }

